def test_text_solutions(capfd):
    draw("peter_griffin", "I'm The Most Non-Competitive. So I Win.")
    out, _ = capfd.readouterr()
    assert out == peter_griffin_text_solution
    draw("snoopy", "All you need is Love. But a little chocolate every now and then doesn't hurt.")
    out, _ = capfd.readouterr()
    assert out == snoopy_text_solution

def test_no_text_solutions(capfd):
    draw("peter_griffin")
    out, _ = capfd.readouterr()
    assert out == peter_griffin_no_text_solution
    draw("snoopy")
    out, _ = capfd.readouterr()
    assert out == snoopy_no_text_solution

def test_invalid_characters():
    with pytest.raises(ValueError):
        draw("anonymouscoolguy")
    with pytest.raises(ValueError):
        draw("snoppi")
        
    