#!/bin/bash

mkdir ~/.aws
touch ~/.aws/credentials
touch ~/.aws/config

echo -ne "[default] \naws_access_key_id = $AWS_ACCESS_KEY_ID \naws_secret_access_key = $AWS_SECRET_ACCESS_KEY " > ~/.aws/credentials
echo -ne "[default] \nregion = $AWS_DEFAULT_REGION \noutput = $AWS_DEFAULT_OUTPUT " > ~/.aws/config